import { combineReducers, configureStore } from '@reduxjs/toolkit';

const initialState = {
    number: ['Bàn phím', 'Chuột', 'Tai nghe'],
    editStatus: true
}

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case 'THÊM_LOA_MỚI':
            return { ...state, number: [...state.number, 'Loa'] }

        case 'ADD_ITEM':
            return { ...state, number: [...state.number, action.addItem] }

        case 'REMOVE_ITEM':
            return { ...state, number: state.number.filter((value, index) => index !== action.removeItem) }

        default:
            return state;
    }
}

const numInitialState = ['Bàn phím', 'Chuột', 'Tai nghe']
const numReducer = (state = numInitialState, action) => {
    switch (action.type) {
        case 'ADD_ITEM':
            return [...state, action.addItem]

        case 'REMOVE_ITEM':
            return [state.filter((value, index) => index !== action.removeItem)]
        default:
            return state
    }
}

const editStatusInitialState = false;
const editStatusReducer = (state = editStatusInitialState, action) => {
    switch (action.type) {
        case 'CHANGE_STATUS':
            return !state
        default:
            return state
    }
}

const allReducers = combineReducers({
    num: numReducer,
    editStatus: editStatusReducer
})

const store = configureStore({ reducer: allReducers });

store.subscribe(() => {
    console.log(JSON.stringify(store.getState()));
})

store.dispatch({ type: 'CHANGE_STATUS' });

// Thêm item
store.dispatch({
    type: 'ADD_ITEM',
    addItem: 'Màn hình'
});

// Xóa item
store.dispatch({
    type: 'REMOVE_ITEM',
    removeItem: 1
})

export default store;
